    from salary_sheets
    inner join salary_sheets_r on salary_sheets.id = salary_sheets_r.ids

    where
        (salary_sheets_r.idp = :pidp) AND
        (((salary_sheets.yr - 1900)*13 + salary_sheets.mt) between :km1 and :km2) AND
        (salary_sheets_r.is_temp = 0) AND
        (salary_sheets.kind = 0)


    union all

    select
        pastdata.id,
        0,
        pastdata.idp as ridp,
        -1,
        pastdata.yr as ryr,
        pastdata.mt as rmt,
        pastdata.caldays,
        pastdata.daysmt, 
        pastdata.hoursmt,
        pastdata.plandays,
        pastdata.planhours,
        pastdata.bruto2,
        pastdata.bruto,
        pastdata.pay,
        pastdata.days,
        pastdata.hours,
        0,
        0

    from pastdata
    where ((pastdata.idp = :PIDP) and
        (((pastdata.yr - 1900)*13 + pastdata.mt) between :km1 and :km2))

    into
        :id,
        :type_tag,
        :idp,
        :idam,
        :yr, 
        :mt,
        :calendar_days,
        :month_workdays,
        :month_workhours,
        :plan_days,
        :plan_hours,
        :total_before_taxes,
        :foravpaycalc_bruto,
        :foravpaycalc_payout,
        :foravpaycalc_days,
        :foravpaycalc_hours,
        :avpaycalc_day,
        :avpaycalc_hour
  do
  begin
    suspend;
  end
