ALTER PROCEDURE SP_GET_MINMAX
RETURNS (
    RDLMINYR INTEGER,
    RDLMINMT INTEGER,
    RDLMAXYR INTEGER,
    RDLMAXMT INTEGER,
    RAMINYR INTEGER,
    RAMINMT INTEGER,
    RAMAXYR INTEGER,
    RAMAXMT INTEGER)
AS
    declare k1 integer;
begin

    RDLMINYR = 0;
    RDLMINMT = 0;
    RDLMAXYR = 0;
    RDLMAXMT = 0;
    RAMINYR = 0;
    RAMINMT = 0;
    RAMAXYR = 0;
    RAMAXMT = 0;

    select min(YR * 13 + MT)
    from salary_sheets
    into :k1;

    if (k1 is null) then
        k1 = 0;
    else
        select min(yr), min(mt)
        from salary_sheets
        where (yr * 13 + mt) = :k1
