            salary_sheets_r.ids = salary_sheets.id)

    where salary_sheets_r.id = :PIDSHR
    into
        :V_SHEET_DT1,
        :V_SHEET_DT2,
        :V_IIN_RATE,
        :V_SI_RATE,
        :V1_PAY_TAXED,
        :V1_PAY_NOSAI,
        :V1_PAY_NOTTAXED,
        :V1_NOTPAID_TAXED,
        :V1_NOTPAID_NOSAI,
        :V1_NOTPAID_NOTTAXED,
        :V1_DNSI,
        :V1_UNTAXED_MINIMUM,
        :V1_IINEX_DEPENDANTS,
        :V1_IINEX2,
        :V1_IINEX2_TP,
        :V1_PF_T,
        :V1_LI_T,
        :V1_HI_T,
        :V1_PF_NT,
        :V1_LI_NT,
        :V1_HI_NT,
        :V1_IINEX_EXP,
        :V1_IIN,
        :V1_PAY;

    V2_PAY_TAXED = 0.0;
    V2_PAY_NOSAI = 0.0;
    V2_PAY_NOTTAXED = 0.0;
    V2_DNSI = 0.0;
    V2_UNTAXED_MINIMUM = 0.0;
