    V2_IINEX_EXP = 0.0;
    V2_IIN = 0.0;
    V2_PAY = 0.0;


    if (V1_PAY is null) then
    begin
        V_IIN_RATE = 0.0;
        V_SI_RATE = 0.0;
        V1_PAY_TAXED = 0.0;
        V1_PAY_NOSAI = 0.0;
        V1_PAY_NOTTAXED = 0.0;
        V1_NOTPAID_TAXED = 0.0;
        V1_NOTPAID_NOSAI = 0.0;
        V1_NOTPAID_NOTTAXED = 0.0;
        V1_DNSI = 0.0;
