ALTER PROCEDURE SP_REP_IINK_01 (
    PDT1 DATE,
    PDT2 DATE)
RETURNS (
    IDP TYPE OF COLUMN SALARY_SHEETS_R.IDP,
    DT1 DATE,
    DT2 DATE,
    INCOME TYPE OF COLUMN SALARY_SHEETS_R.SALARY,
    PLUS_NOTTAXED TYPE OF COLUMN SALARY_SHEETS_R.PLUS_NOTTAXED,
    DNSN_AMOUNT TYPE OF COLUMN SALARY_SHEETS_R.DNSN_AMOUNT,
    UNTAXED_MINIMUM0 TYPE OF COLUMN SALARY_SHEETS_R.IIN_EXEMPT_UNTAXED_MINIMUM0,
    IIN_EXEMPT_DEPENDANTS0 TYPE OF COLUMN SALARY_SHEETS_R.IIN_EXEMPT_DEPENDANTS0,
    IIN_EXEMPT_20 TYPE OF COLUMN SALARY_SHEETS_R.IIN_EXEMPT_20,
    UNTAXED_MINIMUM TYPE OF COLUMN SALARY_SHEETS_R.IIN_EXEMPT_UNTAXED_MINIMUM,
    IIN_EXEMPT_DEPENDANTS TYPE OF COLUMN SALARY_SHEETS_R.IIN_EXEMPT_DEPENDANTS,
    IIN_EXEMPT_2 TYPE OF COLUMN SALARY_SHEETS_R.IIN_EXEMPT_2,
    IIN_EXEMPT_2TP TYPE OF COLUMN SALARY_SHEETS_R.IIN_EXEMPT_2TP,
    IIN_EXEMPT_EXPENSES TYPE OF COLUMN SALARY_SHEETS_R.IIN_EXEMPT_EXPENSES,
    PLUS_PF_NOTTAXED TYPE OF COLUMN SALARY_SHEETS_R.PLUS_PF_NOTTAXED,
    PLUS_LI_NOTTAXED TYPE OF COLUMN SALARY_SHEETS_R.PLUS_LI_NOTTAXED,
    PLUS_HI_NOTTAXED TYPE OF COLUMN SALARY_SHEETS_R.PLUS_HI_NOTTAXED,
    IIN_AMOUNT TYPE OF COLUMN SALARY_SHEETS_R.IIN_AMOUNT,
    IIN_AMOUNT2 TYPE OF COLUMN SALARY_SHEETS_R.IIN_AMOUNT,
    PAY TYPE OF COLUMN SALARY_SHEETS_R.PAY,
    PAY2 TYPE OF COLUMN SALARY_SHEETS_R.PAY,
    ADVANCE TYPE OF COLUMN SALARY_SHEETS_R.ADVANCE)
AS
begin
    for select
        ridp,
        :pdt1 as dt1,
        :pdt2 as dt2,
        sum(rincome) as tincome,
        sum(rplus_nottaxed) as tplus_nottaxed,
        sum(rdnsn_amount) as tdnsn_amount,

        sum(runtaxed_minimum0) as tuntaxed_minimum0,
        sum(riin_exempt_dependants0) as tiin_exempt_dependants0,
        sum(riin_exempt_20) as tiin_exempt_20,

        sum(runtaxed_minimum) as tuntaxed_minimum,
        sum(riin_exempt_dependants) as tiin_exempt_dependants,
        sum(riin_exempt_2) as tiin_exempt_2,
        riin_exempt_2tp as tiin_exempt_2tp,
        sum(riin_exempt_expenses) as tiin_exempt_expenses,

        sum(rplus_pf_nottaxed) as tplus_pf_nottaxed,
        sum(rplus_li_nottaxed) as tplus_li_nottaxed,
        sum(rplus_hi_nottaxed) as tplus_hi_nottaxed,
        sum(riin_amount) as tiin_amount,
        sum(riin_amount2) as tiin_amount2,
        sum(rpay) as tpay,
        sum(rpay2) as tpay2,
        sum(radvance) as tadvance
    from
    (

        select
            salary_sheets_r.idp as  ridp,
            salary_sheets_R.total_before_taxes as rincome,
            salary_sheets_r.plus_nottaxed as rplus_nottaxed,
            salary_sheets_r.dnsn_amount as rdnsn_amount,

            salary_sheets_r.iin_exempt_untaxed_minimum0 as runtaxed_minimum0,
            salary_sheets_r.iin_exempt_dependants0 as riin_exempt_dependants0,
            salary_sheets_r.iin_exempt_20 as riin_exempt_20,

            salary_sheets_r.iin_exempt_untaxed_minimum as runtaxed_minimum,
            salary_sheets_r.iin_exempt_dependants as riin_exempt_dependants,
            salary_sheets_r.iin_exempt_2 as riin_exempt_2,
            salary_sheets_r.iin_exempt_2tp as riin_exempt_2tp,
            salary_sheets_r.iin_exempt_expenses as riin_exempt_expenses,

            salary_sheets_r.plus_pf_nottaxed as rplus_pf_nottaxed,
            salary_sheets_r.plus_li_nottaxed as rplus_li_nottaxed,
            salary_sheets_r.plus_hi_nottaxed as rplus_hi_nottaxed,
            salary_sheets_r.iin_amount as riin_amount,
            0.00 as riin_amount2,
            salary_sheets_r.pay as rpay,
            0.00 as rpay2,
            0.00 as radvance
    
        from salary_sheets
        inner join salary_sheets_r on salary_sheets.id = salary_sheets_r.ids
    
        where
            (salary_sheets.dt2 between :pdt1 and :pdt2) AND
            (salary_sheets_r.is_temp = 0) AND
            (salary_sheets.kind = 0)

        union all

        select 
            paylists_r.idp as ridp,
            0.00 as rincome,
            0.00 as rplus_nottaxed,
            0.00 as rdnsn_amount,

            0.00 as runtaxed_minimum0,
            0.00 as riin_exempt_dependants0,
            0.00 as riin_exempt_20,

            0.00 as runtaxed_minimum,
            0.00 as riin_exempt_dependants,
            0.00 as riin_exempt_2,
            0    as riin_exempt_2tp,
            0.00 as riin_exempt_expenses,
            0.00 as rplus_pf_nottaxed,
            0.00 as rplus_li_nottaxed,
            0.00 as rplus_hi_nottaxed,
            0.00  as riin_amount,
            paylists_r.iin as riin_amount2,
            0.00 as rpay,
            paylists_r.pay as rpay2,
            paylists_r.advance as radvance
    
        from paylists inner join paylists_r on paylists.id = paylists_r.ids
        where paylists.dt between :pdt1 and :pdt2

    )
    group by ridp, riin_exempt_2tp
    order by ridp

    into
        :idp,
        :dt1,
        :dt2,
        :income,
        :plus_nottaxed,
        :dnsn_amount,

        :untaxed_minimum0,
        :iin_exempt_dependants0,
        :iin_exempt_20,

        :untaxed_minimum,
        :iin_exempt_dependants,
        :iin_exempt_2,

        :iin_exempt_2tp,
        :iin_exempt_expenses,
        :plus_pf_nottaxed,
        :plus_li_nottaxed,
        :plus_hi_nottaxed,
        :iin_amount,
        :iin_amount2,
        :pay,
        :pay2,
        :advance
  do
  begin
    if ((:income > 0.00) or (:plus_nottaxed > 0.00) or
        (:iin_amount > 0.00) or (:iin_amount2 > 0.00)) then
        begin
            suspend;
        end
  end
end^


