ALTER PROCEDURE SP_REP_IINK_L1 (
    PDT1 DATE,
    PDT2 DATE,
    PDTX DATE)
RETURNS (
    ID TYPE OF COLUMN SALARY_SHEETS_R.ID,
    IDP TYPE OF COLUMN SALARY_SHEETS_R.IDP,
    IDAM TYPE OF COLUMN SALARY_SHEETS_R.IDAM,
    PAY TYPE OF COLUMN SALARY_SHEETS_R.PAY,
    R DOUBLE PRECISION)
AS
    declare variable payf type of column SALARY_SHEETS_R.PAY;
begin
-- visiem darbiniekiem, neatkargi no t vai atlaists
    for select
        rid as tid,
        max(ridp) as tidp,
        max(ridam) as tidam,
        max(rpayf) as tpayf,
        sum(rpay) as tpay
    from
    (
        select
            salary_sheets_r.id as rid,
            salary_sheets_r.idp as ridp,
            salary_sheets_r.idam as ridam,
            salary_sheets_r.pay as rpayf,
            iif(salary_sheets.dt1 > paylists_r.dt1 and
                salary_sheets.dt2 < paylists_r.dt2,
                salary_sheets_r.pay,
                iif(salary_sheets.dt1 = paylists_r.dt1,
                    minvalue(salary_sheets_r.pay, paylists_r.s1),
                    iif(salary_sheets.dt2 = paylists_r.dt2 and paylists_r.s2 > 0.0,
                        minvalue(salary_sheets_r.pay, paylists_r.s2), 0.0
                    )
                )
            ) as rpay

        from salary_sheets
        inner join salary_sheets_r
            on salary_sheets.id = salary_sheets_r.ids and
                salary_sheets.dt2 between :pdt1 and :pdt2 AND
                salary_sheets.is_temp = 0 AND
                salary_sheets_r.pay <> 0.0 AND
                salary_sheets.kind = 0

        inner join paylists_r
            on paylists_r.idam = salary_sheets_r.idam and
                salary_sheets.dt2 between paylists_r.dt1 and paylists_r.dt2

        inner join paylists
            on paylists.id = paylists_r.ids and
                paylists.dt <= :pdtx

    )
    group by rid
    into :id, :idp, :idam, :pay, :payf
    do
    begin
        if(payf = 0.0)
        then r = 0.0;
        else r = pay / payf;

        suspend;
    end
end^


