ALTER PROCEDURE SP_REP_PDT (
    PDT DATE)
RETURNS (
    IDP INTEGER,
    DATE1 DATE,
    DATE2 DATE)
AS
begin
/* darbinieku pdjie [pieemts - *] datumi */

    for select
        idp, 
        date1,
        date2
    from
    (
        select
            EV.idp,
            EV.date1 as DATE1,
            :pdt as DATE2
        from events as EV
        inner join
        (
            select
                EV2.idp,
                max(EV2.date1) as xdate
            from events as EV2
            where
                EV2.idn in (1, 2) and
                EV2.date1 <= :pdt
            group by EV2.idp
        ) as HT
        on EV.idp = HT.idp and EV.date1 = HT.xdate
        where EV.idn = 1
    
        union all
    
        select
            FT.idp,
            max(EV.date1) as DATE1,
            FT.fdate as DATE2
        from
            events as EV
            inner join 
            (
                select EV3.idp, FT2.xdate as fdate
                from events as EV3
                inner join
                (
                    select
                        EV2.idp,
                        max(EV2.date1) as xdate
                    from events as EV2
                    where
                        EV2.idn in (1, 2) and
                        EV2.date1 <= :pdt
                    group by EV2.idp
                ) as FT2
                on EV3.idp = FT2.idp and EV3.date1 = FT2.xdate
                where EV3.idn = 2
            ) as FT
            on (FT.idp = EV.idp)
    
        where
            EV.idn = 1 and
            EV.date1 < FT.fdate
        group by FT.idp, FT.fdate
    )

    into IDP, DATE1, DATE2

    do
    begin
        suspend;
    end
end^


