ALTER PROCEDURE SP_STATS_01 (
    PDT1 DATE,
    PDT2 DATE)
RETURNS (
    HIRED INTEGER,
    FIRED INTEGER,
    WORKING INTEGER,
    POS_ADDED INTEGER,
    POS_REMOVED INTEGER,
    POS_NO_TIME_TEMPL INTEGER,
    POS_NO_TIME INTEGER,
    POS_NO_SALARY_TEMPL INTEGER,
    POS_NO_SALARY INTEGER,
    POS_NO_PAY_TEMPL INTEGER,
    POS_NO_PAY INTEGER,
    SALARY_PAY DOM_MONEY,
    PAID DOM_MONEY,
    SALARY_PAY_PAID DOM_MONEY)
AS
begin
    select
        coalesce(count(idp), 0)
    from
    (
        select distinct persons.id as idp
        from persons
        inner join events on persons.id = events.idp
        where events.idn = 1 and events.date1 between :pdt1 and :pdt2
    )
    into :HIRED;

    select
        coalesce(count(idp), 0)
    from
    (
        select distinct persons.id as idp
        from persons
        inner join events on persons.id = events.idp
        where events.idn = 2 and events.date1 between :pdt1 and :pdt2
    )
    into :FIRED;


    select
        coalesce(count(idp), 0)
    from
    (
        select
            HT.idp as idp,
            HT.dt as hdate,
            iif(FT.dt is null or HT.dt is null or FT.dt < HT.dt, null, FT.dt) as fdate
        from
        (
            (
                select distinct events.idp as idp, max(events.date1) as dt
                from events
                where events.idn = 1 and events.date1 <= :pdt2
                group by idp
            ) as HT
            left join
            (
                select distinct events.idp as idp, max(events.date1) as dt
                from events
                where events.idn = 2 and events.date1 <= :pdt2
                group by idp
            ) as FT
            on HT.idp = FT.idp
        )
    )
    where hdate is not null and (fdate is null or fdate >= :pdt1)
    into :WORKING;


    select
        coalesce(count(idp), 0)
    from
    (
        select distinct positions.idp as idp
        from positions
        inner join events on positions.id = events.ida
        where events.idn = 3 and events.date1 between :pdt1 and :pdt2
    )
    into :POS_ADDED;

    select
        coalesce(count(idp), 0)
    from
    (
        select distinct positions.idp as idp
        from positions
        inner join events on positions.id = events.ida
        where events.idn = 4 and events.date1 between :pdt1 and :pdt2
    )
    into :POS_REMOVED;


    insert into temp_rep_inkp
    select ida as idp, hdate, fdate
    from
    (
        select
            HT.ida as ida,
            HT.dt as hdate,
            iif(FT.dt is null or HT.dt is null or FT.dt < HT.dt, null, FT.dt) as fdate
        from
        (
            (
                select distinct events.ida as ida, max(events.date1) as dt
                from events
                where events.idn = 3 and events.date1 <= :pdt2
                group by ida
            ) as HT
            left join
            (
                select distinct events.ida as ida, max(events.date1) as dt
                from events
                where events.idn = 4 and events.date1 <= :pdt2
                group by ida
            ) as FT
            on HT.ida = FT.ida
        )
    )
    where hdate is not null and (fdate is null or fdate >= :pdt1);

    select coalesce(count(temp_rep_inkp.idp), 0) as ct
    from temp_rep_inkp
    left join timesheet_templ_r
        on temp_rep_inkp.idp = timesheet_templ_r.idam
    where timesheet_templ_r.id is null
    into :POS_NO_TIME_TEMPL;


    select coalesce(count(temp_rep_inkp.idp), 0) as ct
    from temp_rep_inkp
    left join
    (
        timesheet_lists_r
        inner join timesheet_lists
            on timesheet_lists.id = timesheet_lists_r.ids and
                timesheet_lists.yr = extract(year from :pdt1) and
                timesheet_lists.mt = extract(month from :pdt1)
    )
        on temp_rep_inkp.idp = timesheet_lists_r.idam
    where timesheet_lists_r.id is null
    into :POS_NO_TIME;


    select coalesce(count(temp_rep_inkp.idp), 0) as ct
    from temp_rep_inkp
    left join salary_sheet_templ_r
        on temp_rep_inkp.idp = salary_sheet_templ_r.idam
    where salary_sheet_templ_r.id is null
    into :POS_NO_SALARY_TEMPL;


    select coalesce(count(temp_rep_inkp.idp), 0) as ct
    from temp_rep_inkp
    left join
    (
        salary_sheets_r
        inner join salary_sheets
            on salary_sheets.id = salary_sheets_r.ids and
                salary_sheets.yr = extract(year from :pdt1) and
                salary_sheets.mt = extract(month from :pdt1)
    )
        on temp_rep_inkp.idp = salary_sheets_r.idam
    where salary_sheets_r.id is null
    into :POS_NO_SALARY;


    select coalesce(count(temp_rep_inkp.idp), 0) as ct
    from temp_rep_inkp
    left join paylist_templ_r
        on temp_rep_inkp.idp = paylist_templ_r.idam
    where paylist_templ_r.id is null
    into :POS_NO_PAY_TEMPL;


    select coalesce(count(temp_rep_inkp.idp), 0) as ct
    from temp_rep_inkp
    left join
    (
        paylists_r
        inner join paylists
            on paylists.id = paylists_r.ids and
                paylists.yr = extract(year from :pdt1) and
                paylists.mt = extract(month from :pdt1)
    )
        on temp_rep_inkp.idp = paylists_r.idam

    where paylists_r.id is null
    into :POS_NO_PAY;

    select coalesce(sum(salary_sheets_r.pay), 0.0) as pay
    from salary_sheets
    inner join salary_sheets_r on salary_sheets.id = salary_sheets_r.ids
    where salary_sheets.dt1 >= :pdt1 and salary_sheets.dt2 <= :pdt2
    into :SALARY_PAY;

    select coalesce(sum(paylists_r.pay), 0.0) as pay
    from paylists
    inner join paylists_r on paylists.id = paylists_r.ids
    where paylists.dt between :pdt1 and :pdt2
    into :PAID;


    select coalesce(sum(pay), 0.0) as pay
    from
    (
        select
            salary_sheets_r.pay as pay

        from paylists_r
        inner join salary_sheets_r on salary_sheets_r.idam = paylists_r.idam
        inner join salary_sheets
            on (salary_sheets_r.ids = salary_sheets.id and
                salary_sheets.kind = 0 and
                salary_sheets.dt1 >= :pdt1 and
                salary_sheets.dt2 <= :pdt2 and
                (
                    (salary_sheets.dt1 > paylists_r.dt1 and
                    salary_sheets.dt2 < paylists_r.dt2) or

                    (salary_sheets.dt1 = paylists_r.dt1 and
                    salary_sheets_r.pay = paylists_r.s1) or

                    (salary_sheets.dt2 = paylists_r.dt2 and
                    salary_sheets_r.pay = paylists_r.s2)
                ))

    
        union all
    
        select
            S1 as PAY
    
        from paylists_r
        inner join salary_sheets_r
            on (paylists_r.id_shr_1 is not null and
                paylists_r.id_shr_1 = salary_sheets_r.id)
        inner join salary_sheets
            on (salary_sheets.id = salary_sheets_r.ids and
                salary_sheets.dt1 >= :pdt1 and
                salary_sheets.dt2 <= :pdt2)

    
        union all
    
        select
            S2 as PAY
    
        from paylists_r
        inner join salary_sheets_r
            on (paylists_r.id_shr_2 is not null and
                paylists_r.id_shr_2 = salary_sheets_r.id)
        inner join salary_sheets
            on (salary_sheets.id = salary_sheets_r.ids and
                salary_sheets.dt1 >= :pdt1 and
                salary_sheets.dt2 <= :pdt2)

    )
    into :SALARY_PAY_PAID;



    suspend;
end^


ALTER PROCEDURE SP_STATS_02 (
    PDT1 DATE,
    PDT2 DATE)
RETURNS (
    IDP INTEGER,
    IDAM INTEGER,
    FNAME VARCHAR(20),
    LNAME VARCHAR(20),
    POSTITLE VARCHAR(50),
    TAG INTEGER)
AS
begin

    insert into temp_rep_inkp
    select ida as idp, hdate, fdate
    from
    (
        select
            HT.ida as ida,
            HT.dt as hdate,
            iif(FT.dt is null or HT.dt is null or FT.dt < HT.dt, null, FT.dt) as fdate
        from
        (
            (
                select distinct events.ida as ida, max(events.date1) as dt
                from events
                where events.idn = 3 and events.date1 <= :pdt2
                group by ida
            ) as HT
            left join
            (
                select distinct events.ida as ida, max(events.date1) as dt
                from events
                where events.idn = 4 and events.date1 <= :pdt2
                group by ida
            ) as FT
            on HT.ida = FT.ida
        )
    )
    where hdate is not null and (fdate is null or fdate >= :pdt1);



    for select
        coalesce(xt.idp, persons2.id) as idp,
        xt.idam,
        xt.tag,
        coalesce(persons.fname, persons2.fname) as fname,
        coalesce(persons.lname, persons2.lname) as lname,
        positions.title as postitle
    from
    (

        select distinct persons.id as idp, null as idam, 1 as tag
        from persons
        inner join events on persons.id = events.idp
        where events.idn = 1 and events.date1 between :pdt1 and :pdt2
    
        union all
    
        select distinct persons.id as idp, null as idam, 2 as tag
        from persons
        inner join events on persons.id = events.idp
        where events.idn = 2 and events.date1 between :pdt1 and :pdt2
    
        union all
    
        select
            idp, null as idam, 3 as tag
        from
        (
            select
                HT.idp as idp,
                HT.dt as hdate,
                iif(FT.dt is null or HT.dt is null or FT.dt < HT.dt, null, FT.dt) as fdate
            from
            (
                (
                    select distinct events.idp as idp, max(events.date1) as dt
                    from events
                    where events.idn = 1 and events.date1 <= :pdt2
                    group by idp
                ) as HT
                left join
                (
                    select distinct events.idp as idp, max(events.date1) as dt
                    from events
                    where events.idn = 2 and events.date1 <= :pdt2
                    group by idp
                ) as FT
                on HT.idp = FT.idp
            )
        )
        where hdate is not null and (fdate is null or fdate >= :pdt1)
    
        union all
    
        select distinct positions.idp as idp, positions.id as ida, 4 as tag
        from positions
        inner join events on positions.id = events.ida
        where events.idn = 3 and events.date1 between :pdt1 and :pdt2
    
    
        union all
    
    
        select distinct positions.idp as idp, positions.id as ida, 5 as tag
        from positions
        inner join events on positions.id = events.ida
        where events.idn = 4 and events.date1 between :pdt1 and :pdt2
    
    
        union all
    
    
    
        select positions.idp as idp, temp_rep_inkp.idp as ida, 6 as tag
        from temp_rep_inkp
        inner join positions on positions.id = temp_rep_inkp.idp
        left join timesheet_templ_r
            on temp_rep_inkp.idp = timesheet_templ_r.idam
        where timesheet_templ_r.id is null
    
        union all
    
    
        select positions.idp as idp, temp_rep_inkp.idp as ida, 7 as tag
        from temp_rep_inkp
        inner join positions on positions.id = temp_rep_inkp.idp
        left join
        (
            timesheet_lists_r
            inner join timesheet_lists
                on timesheet_lists.id = timesheet_lists_r.ids and
                    timesheet_lists.yr = extract(year from :pdt1) and
                    timesheet_lists.mt = extract(month from :pdt1)
        )
            on temp_rep_inkp.idp = timesheet_lists_r.idam
        where timesheet_lists_r.id is null
    
    
        union all
    
        select positions.idp as idp, temp_rep_inkp.idp as ida, 8 as tag
        from temp_rep_inkp
        inner join positions on positions.id = temp_rep_inkp.idp
        left join salary_sheet_templ_r
            on temp_rep_inkp.idp = salary_sheet_templ_r.idam
        where salary_sheet_templ_r.id is null
    
        union all
    
    
        select salary_sheets_r.idp as idp, temp_rep_inkp.idp as ida, 9 as tag
        from temp_rep_inkp
        left join
        (
            salary_sheets_r
            inner join salary_sheets
                on salary_sheets.id = salary_sheets_r.ids and
                    salary_sheets.yr = extract(year from :pdt1) and
                    salary_sheets.mt = extract(month from :pdt1)
        )
            on temp_rep_inkp.idp = salary_sheets_r.idam
        where salary_sheets_r.id is null
    
        union all
    
        select positions.idp as idp, temp_rep_inkp.idp as ida, 10 as tag
        from temp_rep_inkp
        inner join positions on positions.id = temp_rep_inkp.idp
        left join paylist_templ_r
            on temp_rep_inkp.idp = paylist_templ_r.idam
        where paylist_templ_r.id is null
    
        union all
    
        select paylists_r.idp as idp, temp_rep_inkp.idp as ida, 11 as tag
        from temp_rep_inkp
        left join
        (
            paylists_r
            inner join paylists
                on paylists.id = paylists_r.ids and
