ALTER PROCEDURE SP_TIMESHEET_LISTS_01 (
    PYR1 INTEGER,
    PMT1 INTEGER,
    PYR2 INTEGER,
    PMT2 INTEGER)
RETURNS (
    ID TYPE OF COLUMN TIMESHEET_LISTS.ID,
    YR TYPE OF COLUMN TIMESHEET_LISTS.YR,
    MT TYPE OF COLUMN TIMESHEET_LISTS.MT,
    SNR TYPE OF COLUMN TIMESHEET_LISTS.SNR,
    DESCR TYPE OF COLUMN TIMESHEET_LISTS.DESCR,
    IDSH TYPE OF COLUMN TIMESHEET_LISTS.IDSH,
    DEP TYPE OF COLUMN TIMESHEET_LISTS.DEP)
AS
    declare km1 integer;
    declare km2 integer;
begin

    km1 = (:pyr1 - 1900) * 13 + :pmt1;
    km2 = (:pyr2 - 1900) * 13 + :pmt2;

    for select
