    MONTH_WORKDAYS TYPE OF COLUMN SALARY_SHEETS_R.MONTH_WORKDAYS,
    MONTH_WORKHOURS TYPE OF COLUMN SALARY_SHEETS_R.MONTH_WORKHOURS,
