ALTER PROCEDURE SP_PAY_MATCHLISTS_21 (
    PID INTEGER)
RETURNS (
    ID_SHR INTEGER,
    DT1 DATE,
    DT2 DATE,
    IIN_RATE DOM_MONEY,
    SI_RATE DOM_MONEY,
    PAY_TAXED DOM_MONEY,
    PAY_NOSAI DOM_MONEY,
    PAY_NOTTAXED DOM_MONEY,
    NOTPAID_TAXED DOM_MONEY,
    NOTPAID_NOSAI DOM_MONEY,
    NOTPAID_NOTTAXED DOM_MONEY,
    DNSI DOM_MONEY,
    UNTAXED_MINIMUM DOM_MONEY,
    IINEX_DEPENDANTS DOM_MONEY,
    IINEX2 DOM_MONEY,
    IINEX2_TP SMALLINT,
    PF_T DOM_MONEY,
    LI_T DOM_MONEY,
    HI_T DOM_MONEY,
    PF_NT DOM_MONEY,
    LI_NT DOM_MONEY,
    HI_NT DOM_MONEY,
    IINEX_EXP DOM_MONEY,
    IIN DOM_MONEY,
    PAY DOM_MONEY)
AS
begin
    for select
        ID_SHR,
        DT1,
        DT2,
        RATE_IIN,
        RATE_DNSN,
        PAY_TAXED,
        PAY_NOSAI,
        PAY_NOTTAXED,
        NOTPAID_TAXED,
        NOTPAID_NOSAI,
        NOTPAID_NOTTAXED,
        DNSI,
        UNTAXED_MINIMUM,
        IINEX_DEPENDANTS,
        IINEX2,
        IINEX2_TP,
        PF_T,
        LI_T,
        HI_T,
        PF_NT,
        LI_NT,
        HI_NT,
        IINEX_EXP,
        IIN,
        PAY
    from
    (
         select
            salary_sheets_r.id as ID_SHR,
            salary_sheets.dt1 as dt1,
            salary_sheets.dt2 as dt2,
            salary_sheets_r.rate_iin as rate_iin,
            salary_sheets_r.rate_dnsn as rate_dnsn,
            salary_sheets_r.salary +
                salary_sheets_r.plus_taxed +
                salary_sheets_r.vacation_pay_current +
                salary_sheets_r.sickdays_pay AS PAY_TAXED,
    
            salary_sheets_r.plus_nosai AS PAY_NOSAI,
    
            salary_sheets_r.plus_nottaxed +
                salary_sheets_r.plus_pf_nottaxed +
                salary_sheets_r.plus_li_nottaxed +
                salary_sheets_r.plus_hi_nottaxed AS PAY_NOTTAXED,

            salary_sheets_r.plus_np_taxed as NOTPAID_TAXED,
            salary_sheets_r.plus_np_nosai as NOTPAID_NOSAI,
            salary_sheets_r.plus_np_nottaxed as NOTPAID_NOTTAXED,

            salary_sheets_r.dnsn_amount as DNSI,
    
            salary_sheets_r.iin_exempt_untaxed_minimum AS UNTAXED_MINIMUM,
            salary_sheets_r.iin_exempt_dependants AS IINEX_DEPENDANTS,
            salary_sheets_r.iin_exempt_2 AS IINEX2,
            salary_sheets_r.iin_exempt_2tp AS iinex2_tp,
            salary_sheets_r.plus_pf_taxed AS PF_T,
            salary_sheets_r.plus_li_taxed AS LI_T,
            salary_sheets_r.plus_hi_taxed AS HI_T,
            salary_sheets_r.plus_pf_nottaxed AS PF_NT,
            salary_sheets_r.plus_li_nottaxed AS LI_NT,
            salary_sheets_r.plus_hi_nottaxed AS HI_NT,
            salary_sheets_r.iin_exempt_expenses AS IINEX_EXP,
            salary_sheets_r.iin_amount AS IIN,
            salary_sheets_r.pay AS PAY
    
        from paylists_r
        inner join paylists on(paylists_r.ids = paylists.id and paylists_r.id = :PID)
        inner join salary_sheets_r on salary_sheets_r.idam = paylists_r.idam
        inner join salary_sheets
            on (salary_sheets_r.ids = salary_sheets.id and
                salary_sheets.kind = 0 and
                (
                    (salary_sheets.dt1 > paylists_r.dt1 and
                    salary_sheets.dt2 < paylists_r.dt2) or

                    (salary_sheets.dt1 = paylists_r.dt1 and
                    salary_sheets_r.pay = paylists_r.s1) or

                    (salary_sheets.dt2 = paylists_r.dt2 and
                    salary_sheets_r.pay = paylists_r.s2)
                ))

    
        union all
    
        select
            id_shr_1 as ID_SHR,
            salary_sheets.dt1 as dt1,
            salary_sheets.dt2 as dt2,
            salary_sheets_r.rate_iin as rate_iin,
            salary_sheets_r.rate_dnsn as rate_dnsn,
            PAY_TAXED_1 as PAY_TAXED,
            PAY_NOSAI_1 as PAY_NOSAI,
            PAY_NOTTAXED_1 as PAY_NOTTAXED,
            0.0 as NOTPAID_TAXED,
            0.0 as NOTPAID_NOSAI,
            0.0 as NOTPAID_NOTTAXED,
            DNSI_1 as DNSI,
            UNTAXED_MINIMUM_1 as UNTAXED_MINIMUM,
            IINEX_DEPENDANTS_1 as IINEX_DEPENDANTS,
            IINEX2_1 as IINEX2,
            IINEX2_TP_1 as iinex2_tp,
            0.0 as PF_T,
            0.0 as LI_T,
            0.0 as HI_T,
            PFNT_1 as PF_NT,
            LINT_1 as LI_NT,
            HINT_1 as HI_NT,
            IINEX_EXP_1 as IINEX_EXP,
            IIN_1 as IIN,
            S1 as PAY
    
        from paylists_r
        inner join salary_sheets_r
            on (paylists_r.id = :PID and
                paylists_r.id_shr_1 is not null and
                paylists_r.id_shr_1 = salary_sheets_r.id)
        inner join salary_sheets on salary_sheets.id = salary_sheets_r.ids
    
        union all
    
        select
            id_shr_2 as ID_SHR,
            salary_sheets.dt1 as dt1,
            salary_sheets.dt2 as dt2,
            salary_sheets_r.rate_iin as rate_iin,
            salary_sheets_r.rate_dnsn as rate_dnsn,
            PAY_TAXED_2 as PAY_TAXED,
            PAY_NOSAI_2 as PAY_NOSAI,
            PAY_NOTTAXED_2 as PAY_NOTTAXED,
            0.0 as NOTPAID_TAXED,
            0.0 as NOTPAID_NOSAI,
            0.0 as NOTPAID_NOTTAXED,
            DNSI_2 as DNSI,
            UNTAXED_MINIMUM_2 as UNTAXED_MINIMUM,
            IINEX_DEPENDANTS_2 as IINEX_DEPENDANTS,
            IINEX2_2 as IINEX2,
            IINEX2_TP_2 as iinex2_tp,
            0.0 as PF_T,
            0.0 as LI_T,
            0.0 as HI_T,
            PFNT_2 as PF_NT,
            LINT_2 as LI_NT,
            HINT_2 as HI_NT,
            IINEX_EXP_2 as IINEX_EXP,
            IIN_2 as IIN,
            S2 as PAY
    
        from paylists_r
        inner join salary_sheets_r
            on (paylists_r.id = :PID and
                paylists_r.id_shr_2 is not null and
                paylists_r.id_shr_2 = salary_sheets_r.id)
        inner join salary_sheets on salary_sheets.id = salary_sheets_r.ids

    )
    order by dt1

    into
        :ID_SHR,
        :DT1,
        :DT2,
        :IIN_RATE,
        :SI_RATE,
        :PAY_TAXED,
        :PAY_NOSAI,
        :PAY_NOTTAXED,
        :NOTPAID_TAXED,
        :NOTPAID_NOSAI,
        :NOTPAID_NOTTAXED,
        :DNSI,
        :UNTAXED_MINIMUM,
        :IINEX_DEPENDANTS,
        :IINEX2,
        :IINEX2_TP,
        :PF_T,
        :LI_T,
        :HI_T,
        :PF_NT,
        :LI_NT,
        :HI_NT,
        :IINEX_EXP,
        :IIN,
        :PAY
    do
    suspend;
end^


ALTER PROCEDURE SP_PAY_SALDO_01 (
    PIDAM INTEGER,
    PDATE DATE,
    PDATET DATE)
RETURNS (
    IDP INTEGER,
    IDAM INTEGER,
    PAY0 DOM_MONEY,
    IIN0 DOM_MONEY,
    ADVANCE DOM_MONEY,
    WITHHOLDINGS DOM_MONEY)
AS
begin
    for select
        0 as tidp,
        :PIDAM as tidam,
        sum(spay) as tpay,
        sum(siin) as tiin,
        sum(sadvance) as tadvance,
        sum(swithholdings) as twithholdings

    from
    (
        select
            salary_sheets_r.pay as spay,
            salary_sheets_r.iin_amount as siin,
            salary_sheets_r.advance as sadvance,
            salary_sheets_r.minus_after_iin as swithholdings
    
        from salary_sheets inner join salary_sheets_r on salary_sheets.id = salary_sheets_r.ids
        where
            salary_sheets_r.idam = :pidam and
            salary_sheets.kind = 0 and
            salary_sheets.dt2 <= :pdate and
            salary_sheets.is_temp = 0

        union all

        select
            spay,
            siin,
            sadvance,
            swithholdings
        from
        (
            select first 1
                salary_sheets.is_temp as stemp,
                0.0 as spay,
                0.0 as siin,
                salary_sheets_r.pay + salary_sheets_r.advance
                    - salary_sheets_r.minus_after_iin as sadvance,
                0.0 as swithholdings

            from salary_sheets inner join salary_sheets_r on salary_sheets.id = salary_sheets_r.ids
            where
                salary_sheets_r.idam = :pidam and
                salary_sheets.kind = 0 and
                salary_sheets.dt2 between :pdatet and :pdate
            order by salary_sheets.dt2 desc
        )
        where stemp = 1

        union all

        select
            pay0 as spay,
            iin0 as siin,
            advance as sadvance,
            0 as swithholdings
        from positions
        where id = :pidam


        union all
    
        select 
             -paylists_r.pay as spay,
             -paylists_r.iin as siin,
             -paylists_r.advance as sadvance,
             -paylists_r.withholdings as swithholdings

        from paylists inner join paylists_r on paylists.id = paylists_r.ids
        where paylists.dt < :pdate and paylists_r.idam = :pidam
    )

    into
        :idp, :idam, :pay0, :iin0, :advance, :withholdings
    do suspend;
end^


ALTER PROCEDURE SP_PAY_SALDO_02 (
    PIDS INTEGER,
