                    minvalue(salary_sheets_r.pay, paylists_r.s1),
                    iif(salary_sheets.dt2 = paylists_r.dt2 and paylists_r.s2 > 0.0,
                        minvalue(salary_sheets_r.pay, paylists_r.s2), 0.0
                    )
                )
            ) as rpay

        from salary_sheets
        inner join salary_sheets_r
            on salary_sheets.id = salary_sheets_r.ids and
                salary_sheets.dt2 between :pdt1 and :pdt2 AND
                salary_sheets_r.is_temp = 0 AND
                salary_sheets_r.pay <> 0.0 AND
                salary_sheets.kind = 0

        inner join sp_rep_iink_p2(:pdt1, :pdt2) as TP
            on TP.idp = salary_sheets_r.idp and
                salary_sheets.dt2 between TP.hdate and TP.fdate

        inner join paylists_r
            on paylists_r.idam = salary_sheets_r.idam and
                salary_sheets.dt2 between paylists_r.dt1 and paylists_r.dt2

        inner join paylists
            on paylists.id = paylists_r.ids and
                paylists.dt <= :pdtx

    )
    group by rid
    into :id, :idp, :idam, :pay, :payf
    do
    begin
        if(payf = 0.0)
        then r = 0.0;
        else r = pay / payf;

        suspend;
    end
end^


ALTER PROCEDURE SP_REP_IINK_P1 (
    PDT1 DATE,
    PDT2 DATE,
    PDTY DATE)
RETURNS (
    IDP INTEGER,
    HDATE DATE,
    FDATE DATE)
AS
begin
/* darbinieki, kas atlaisti norādītajā periodā */

    for select
        FT.idp,
        maxvalue(max(EV.date1), :pdty) as hdate,
        FT.fdate
    from
        events as EV
        inner join 
        (
            select distinct EV3.idp, FT2.xdate as fdate
            from events as EV3
            inner join
            (
                select
                    EV2.idp,
                    max(EV2.date1) as xdate
                from events as EV2
                where
                    EV2.idn in (1, 2) and
                    EV2.date1 between :pdt1 and :pdt2
                group by EV2.idp
            ) as FT2
            on EV3.idp = FT2.idp and EV3.date1 = FT2.xdate
            where EV3.idn = 2
        ) as FT
        on (FT.idp = EV.idp)

    where
        EV.idn = 1 and
        EV.date1 < FT.fdate
    group by FT.idp, FT.fdate

    into IDP, HDATE, FDATE

    do
    begin
        suspend;
    end
end^


ALTER PROCEDURE SP_REP_IINK_P2 (
    PDTY1 DATE,
    PDTY2 DATE)
RETURNS (
    IDP INTEGER,
    HDATE DATE,
    FDATE DATE)
AS
begin
/* darbinieki, kas līdz gada beigām nav atlaisti */

    for select distinct
        EV.idp,
        maxvalue(EV.date1, :pdty1) as HDATE,
        :pdty2 as FDATE
    from events as EV
    inner join
    (
        select
            EV2.idp,
            max(EV2.date1) as xdate
        from events as EV2
        where
            EV2.idn in (1, 2) and
            EV2.date1 <= :pdty2
        group by EV2.idp
    ) as HT
    on EV.idp = HT.idp and EV.date1 = HT.xdate
    where EV.idn = 1

    into IDP, HDATE, FDATE

    do
    begin
        suspend;
    end
end^


ALTER PROCEDURE SP_REP_PDT (
    PDT DATE)
RETURNS (
    IDP INTEGER,
    DATE1 DATE,
    DATE2 DATE)
AS
begin
/* darbinieku pēdējie [pieņemts - *] datumi */

    for select
        idp, 
        date1,
        date2
    from
    (
        select
            EV.idp,
            EV.date1 as DATE1,
            :pdt as DATE2
        from events as EV
        inner join
        (
            select
                EV2.idp,
                max(EV2.date1) as xdate
            from events as EV2
            where
                EV2.idn in (1, 2) and
                EV2.date1 <= :pdt
            group by EV2.idp
        ) as HT
        on EV.idp = HT.idp and EV.date1 = HT.xdate
        where EV.idn = 1
    
        union all
    
        select
            FT.idp,
            max(EV.date1) as DATE1,
            FT.fdate as DATE2
        from
            events as EV
            inner join 
            (
                select EV3.idp, FT2.xdate as fdate
                from events as EV3
                inner join
                (
                    select
                        EV2.idp,
                        max(EV2.date1) as xdate
                    from events as EV2
                    where
                        EV2.idn in (1, 2) and
                        EV2.date1 <= :pdt
                    group by EV2.idp
                ) as FT2
                on EV3.idp = FT2.idp and EV3.date1 = FT2.xdate
                where EV3.idn = 2
            ) as FT
            on (FT.idp = EV.idp)
    
        where
            EV.idn = 1 and
            EV.date1 < FT.fdate
        group by FT.idp, FT.fdate
    )

    into IDP, DATE1, DATE2

    do
    begin
        suspend;
    end
end^


